import java.io.*;//import java.io.StringReader;import java.util.*;import java.util.Iterator;import java.util.Map;import java.util.logging.Level;import java.util.logging.Logger;import javax.xml.transform.*;import javax.xml.transform.Source;import javax.xml.transform.stream.*;import javax.xml.transform.Transformer;import javax.xml.transform.TransformerConfigurationException;import javax.xml.transform.TransformerException;import javax.xml.transform.TransformerFactory;import javax.xml.transform.dom.DOMResult;//import javax.xml.transform.stream.StreamSource;import java.io.File;import org.w3c.dom.Node;public class Create{	static public void main(String []args){		Source xmlInput = new StreamSource(new File("c:/xdiff/Test/test.xml"));		Source xsl = new StreamSource(new File("c:/xdiff/Test/test.xsl"));		Result xmlOutput = new StreamResult(new File("c:/xdiff/Test/Output.xml"));		try {			Transformer transformer = TransformerFactory.newInstance().newTransformer(xsl);			transformer.transform(xmlInput, xmlOutput);		} catch (TransformerException e) {			System.out.println("Error Detected \n");		}	}}